package com.happy.photoviewer.photoviewer.ui;import android.content.Context;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v4.app.Fragment;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import com.happy.photoviewer.R;import com.happy.photoviewer.entities.Photo;import com.happy.photoviewer.libs.base.ImageLoader;import butterknife.BindView;import butterknife.ButterKnife;public class PhotoViewerFragment extends Fragment {    private static final String TAG = PhotoViewerFragment.class.getSimpleName();    private static final String KEY_MODEL = "key_model";    private ImageLoader imageLoader;    @BindView(R.id.image)    ImageView imageView;    @BindView(R.id.title)    TextView titleView;    @BindView(R.id.url)    TextView urlView;    @BindView(R.id.imageSize)    TextView imageSizeView;    @BindView(R.id.dateTaken)    TextView dateTaken;    public PhotoViewerFragment(ImageLoader imageLoader) {        this.imageLoader = imageLoader;    }    public static PhotoViewerFragment newInstance(Photo model, ImageLoader imageLoader) {        PhotoViewerFragment fragment = new PhotoViewerFragment(imageLoader);        Bundle args = new Bundle();        args.putParcelable(KEY_MODEL, model);        fragment.setArguments(args);        return fragment;    }    @Override    public void onAttach(Context context) {        super.onAttach(context);        Log.d(TAG, "state = onAttach");    }    @Override    public void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        Log.d(TAG, "state = onCreate");    }    @Nullable    @Override    public View onCreateView(LayoutInflater inflater, @Nullable ViewGroup container,                             @Nullable Bundle savedInstanceState) {        Log.d(TAG, "state = onCreateView");        View view = inflater.inflate(R.layout.image_viewer, container, false);        ButterKnife.bind(this, view);        Photo photo = getArguments().getParcelable(KEY_MODEL);        titleView.setText(photo.getTitle());        imageSizeView.setText(photo.getWidth() + "x" + photo.getHeight());        urlView.setText(photo.getUrl());        dateTaken.setText(photo.getDateTaken());        Log.d(TAG, "test url="+photo.getUrl());        String url = photo.getUrl();        if (url != null) {            imageLoader.load(imageView, photo.getUrl(), android.R.drawable.ic_dialog_info);        }        return view;    }    @Override    public void onActivityCreated(@Nullable Bundle savedInstanceState) {        super.onActivityCreated(savedInstanceState);        Log.d(TAG, "state = onActivityCreated");    }    @Override    public void onStart() {        super.onStart();        Log.d(TAG, "state = onStart");    }    @Override    public void onResume() {        super.onResume();        Log.d(TAG, "state = onResume");    }    @Override    public void onPause() {        super.onPause();        Log.d(TAG, "state = onPause");    }    @Override    public void onStop() {        super.onStop();        Log.d(TAG, "state = onStop");    }    @Override    public void onDestroyView() {        super.onDestroyView();        Log.d(TAG, "state = onDestroyView");    }    @Override    public void onDestroy() {        super.onDestroy();        Log.d(TAG, "state = onDestroy");    }    @Override    public void onDetach() {        super.onDetach();        Log.d(TAG, "state = onDetach");    }}